mapper.select:
select * from goodskind
where 1=1

  and goodsKindId = #{goodskindid}


  and goodsKindName = #{goodskindname}


  and goodsKindTime = #{goodskindtime}

select * from goodskind
where goodsKindId = #{id}
limit 0,1

