mapper.select:
  select * from userinfo
  where 1=1
  
    and userId = #{userid}
  
  
    and userName = #{username}
  
  
    and password = #{password}
  
  
	  and (userName like CONCAT('%',#{realname},'%')
    or realName like CONCAT('%',#{realname},'%'))
  
  
    and createTime = #{createtime}
  
  
    and wages = #{wages}
  
  
    and userType = #{usertype}
  
  
    and userHeadImgUrl = #{userheadimgurl}
  
  
    and phone = #{phone}
  
  
    and address = #{address}
  
  
    and cardId = #{cardid}
  
  
    and email = #{email}

